mapper.select:
select store_id,store_name, store_x, store_y, ,store_sigungu, store_dong
from 
(select store_id,store_name, store_x, store_y, ,store_sigungu, store_dong
from store where category_id in 

	#{category}
)

where store_name like #{store_name}


where store_dong like #{store_dong}


where email like #{email}
